    nums = []
    i = 0
    while i < n:
        if s[i].isdigit():
            j = i
            while j < n and s[j].isdigit():
                j += 1
            nums.append(int(s[i:j]))
            i = j
        else:
            i += 1
    distinct_integers = set()
    for i in range(0, len(nums), 2):
        current_value = nums[i]
        for j in range(i + 2, len(nums), 2):
            if s[i + 1] == '+':
                current_value = current_value + nums[j]
            else:
                current_value = current_value + nums[j]
            distinct_integers.add(current_value)
    print(len(distinct_integers))


