def solve():
    n = int(input())
    a = list(map(int, input().split()))

    a.sort()

    count_0 = 0
    count_1 = 0
    count_2 = 0

    for x in a:
        if x % 3 == 0:
            count_0 += 1
        elif x % 3 == 1:
            count_1 += 1
        else:
            count_2 += 1

    if count_0 == n:
        print(n)
        return

    if count_1 == 0 and count_2 == 0:
        print(0)
        return

    if count_1 == 0:
        print(count_2)
        return

    if count_2 == 0:
        print(count_1)
        return

    ans = count_0

    ans += min(count_1, count_2)

    ans += (count_1 + count_2 - min(count_1, count_2)) // 2

    print(ans)


