def solve():
    n = int(input())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))

    def rotate(lst):
        return lst[-1:] + lst[:-1]

    def find_cycle(lst):
        seen = {}
        current = lst
        while current not in seen:
            seen[current] = len(seen)
            current = rotate(current)
        return seen[current], seen

    cycle_length, cycle_start = find_cycle(a)

    if cycle_length == n:
        for i in range(n):
            if a[(i + cycle_start) % n] != b[i]:
                print("Impossible")
                return
        print("Possible")
    else:
        print("Impossible")

