def solve():
    n = int(input())
    tree = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        u, v = map(int, input().split())
        tree[u].append(v)
        tree[v].append(u)

    def dfs(node, parent):
        leaves = []
        for child in tree[node]:
            if child != parent:
                leaves.extend(dfs(child, node))
        if not leaves:
            return [node]
        else:
            return leaves

    leaves = dfs(1, 0)

    max_result = 0
    min_result = 0

    for leaf in leaves:
        subtree_numbers = [0] * (n + 1)
        subtree_numbers[leaf] = 1

        def dfs(node):
            nonlocal subtree_numbers
            for child in tree[node]:
                if child != leaf:
                    dfs(child)
                    subtree_numbers[node] += subtree_numbers[child]

        dfs(leaf)

        max_val = 0
        min_val = n

        def dfs(node):
            nonlocal max_val, min_val
            for child in tree[node]:
                if child != node:
                    dfs(child)
                    if subtree_numbers[child] > 0:
                        max_val = max(max_val, subtree_numbers[child] + 1)
                        min_val = min(min_val, subtree_numbers[child] + 1)
                    else:
                        max_val = max(max_val, 1)
                        min_val = min(min_val, 1)

        dfs(leaf)

        max_result = max(max_result, max_val)
        min_result = min(min_result, min_val)

    print(max_result, min_result)

solve()

