import math

class Droplet:
    def __init__(self, x, y, vx, vy, r):
        self.x = x
        self.y = y
        self.vx = vx
        self.vy = vy
        self.r = r
        self.area = math.pi * r**2

def distance(d1, d2):
    return math.sqrt((d1.x - d2.x)**2 + (d1.y - d2.y)**2)

def time_to_collision(d1, d2):
    dx = d2.x - d1.x
    dy = d2.y - d1.y
    dvx = d2.vx - d1.vx
    dvy = d2.vy - d1.vy
    if dvx == 0 and dvy == 0:
        return float('inf')  # Parallel droplets
    return dx * dvy - dy * dvx / (dvx**2 + dvy**2)

def merge_droplets(d1, d2):
    new_x = (d1.x * d1.area + d2.x * d2.area) / (d1.area + d2.area)
    new_y = (d1.y * d1.area + d2.y * d2.area) / (d1.area + d2.area)
    new_r = math.sqrt(d1.r**2 + d2.r**2)
    new_area = d1.area + d2.area
    new_droplet = Droplet(new_x, new_y, 0, 0, new_r)
    return new_droplet

def simulate_droplets(n, droplets, t_max):
    t = 0
    while t < t_max:
        collisions = []
        for i in range(n):
            for j in range(i + 1, n):
                dt = time_to_collision(droplets[i], droplets[j])
                if dt > 0 and dt < t_max:
                    collisions.append((dt, i, j))

        collisions.sort()

        for dt, i, j in collisions:
            if dt == float('inf'):
                continue  # Parallel droplets, do nothing

            d1 = droplets[i]
            d2 = droplets[j]

            new_droplet = merge_droplets(d1, d2)

            droplets.pop(j)
            droplets.pop(i)

            droplets.append(new_droplet)

            t += dt

        if len(droplets) <= 1:
            break

        t = t_max  # Ensure we don't go beyond t_max

    k = len(droplets)
    if k == n:
        t = 0
    return k, t

