    n, u, v = map(int, input().split())
    adj = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        a, b = map(int, input().split())
        adj[a].append(b)
        adj[b].append(a)

    def dfs(cur, par):
        nonlocal depth_u, depth_v
        if cur == u:
            depth_u = 0
        elif cur == v:
            depth_v = 0
        else:
            for nxt in adj[cur]:
                if nxt != par:
                    depth_u = depth_v = float('inf')
                    dfs(nxt, cur)
                    if depth_u < depth_v:
                        depth_v = depth_u + 1
                    else:
                        depth_u = depth_v + 1

    def lca(a, b):
        if depth_u < depth_v:
            a, b = b, a
        diff = depth_u - depth_v
        for _ in range(diff):
            a = par[a]

        if a == b:
            return a

        for i in range(19, -1, -1):
            if par[a][i] != par[b][i]:
                a, b = par[a][i], par[b][i]

        return par[a][0]

    par = [[0] * 20 for _ in range(n + 1)]
    depth = [0] * (n + 1)

    def dfs2(cur, par_cur, depth_cur):
        par[cur][0] = par_cur
        depth[cur] = depth_cur
        for nxt in adj[cur]:
            if nxt != par_cur:
                dfs2(nxt, cur, depth_cur + 1)

    dfs2(1, 0, 0)

    lca_node = lca(u, v)

    print(depth_u + depth_v - 2 * depth[lca_node] - 1)

solve()

MOD = 10**6 + 3


def gcd(a, b):
    if b == 0:
        return a
    return gcd(b, a % b)


def inv(x):
    return pow(x, MOD - 2, MOD)


def comb(n, r):
    a = 1
    b = 1
    for i in range(r):
        a = (a * (n - i)) % MOD
        b = (b * (i + 1)) % MOD
    return (a * inv(b)) % MOD


