def calculate_spaciousness(ranges):
    n = len(ranges)
    if n == 0:
        return 0

    areas = []
    for x, y, r in ranges:
        areas.append(area_of_circle(x, y, r))

    total_area = sum(areas)

    count = [0] * n
    for i in range(n):
        for j in range(i + 1, n):
            x1, y1, r1 = ranges[i]
            x2, y2, r2 = ranges[j]

            dist = distance(x1, y1, x2, y2)

            if dist > r1 + r2:
                continue
            if dist < abs(r1 - r2):
                continue

            overlap_area = 0
            if dist == 0:
                overlap_area = r1**2
            else:
                overlap_rad = (dist**2 + r1**2 + r2**2) / (2 * dist)
                overlap_area = math.pi * overlap_rad**2

            areas[i] -= overlap_area
            areas[j] -= overlap_area

            count[i] += 1
            count[j] += 1

    max_count = max(count)

    if max_count % 2 == 0:
        return total_area
    else:
        return total_area - (max_count // 2) * area_of_circle(0, 0, max_count / 2)

