    n = len(graph)
    dist = [-1] * n
    dist[s] = 0
    queue = [s]

    while queue:
        u = queue.pop(0)
        for v, capacity in graph[u]:
            if dist[v] == -1 and capacity > 0:
                dist[v] = dist[u] + 1
                queue.append(v)

    return dist[t] != -1

