def solve():
    N, S = map(int, input().split())
    A = list(map(int, input().split()))

    mod = 998244353

    ans = 0

    for mask in range(1 << N):
        subset = []
        for i in range(N):
            if (mask >> i) & 1:
                subset.append(i + 1)

        if not subset:
            continue

        subset_sums = []
        for i in range(1 << len(subset)):
            current_sum = 0
            current_subset = []
            for j in range(len(subset)):
                if (i >> j) & 1:
                    current_subset.append(subset[j])
                    current_sum += A[subset[j] - 1]

            if current_subset:
                subset_sums.append(current_sum)

        subset_sums.sort()

        dp = [0] * (S + 1)
        dp[0] = 1

        for s in subset_sums:
            for j in range(S, s - 1, -1):
                dp[j] = (dp[j] + dp[j - s]) % mod

        ans = (ans + dp[S]) % mod

    print(ans)

