def solve():
    n = int(input())
    costs = list(map(int, input().split()))
    m = int(input())
    roads = []
    for _ in range(m):
        u, v = map(int, input().split())
        roads.append((u - 1, v - 1))

    graph = [[] for _ in range(n)]
    for u, v in roads:
        graph[u].append(v)

    def dfs(node, parent):
        subtree_size = 1
        for child in graph[node]:
            if child != parent:
                subtree_size += dfs(child, node)
        return subtree_size

    def find_min_cost(node, parent, visited, min_cost, min_checkposts, min_checkpost_count):
        visited[node] = True

        checkpost_needed = False
        for child in graph[node]:
            if not visited[child]:
                subtree_size = find_min_cost(child, node, visited, min_cost, min_checkposts, min_checkpost_count)

                if subtree_size > 0:
                    min_cost[node] = min(min_cost[node], min_cost[child] + costs[node])
                    checkpost_needed = True
                else:
                    min_cost[node] = min(min_cost[node], costs[node])

        if not checkpost_needed:
            if parent != -1:
                min_checkposts[node] = min(min_checkposts[node], 1)
                min_checkpost_count[node] = 1
            else:
                min_checkposts[node] = 0
                min_checkpost_count[node] = 1
        else:
            min_checkposts[node] = 1
            min_checkpost_count[node] = 1

        visited[node] = False
        return subtree_size

    visited = [False] * n
    min_cost = [float('inf')] * n
    min_checkposts = [0] * n
    min_checkpost_count = [0] * n

    find_min_cost(0, -1, visited, min_cost, min_checkposts, min_checkpost_count)

    print(min_cost[0], (min_checkposts[0] * min_checkpost_count[0]) % 1000000007)

