def solve():
    M, S = map(int, input().split())
    preferences = []
    for _ in range(M):
        preferences.append(list(map(int, input().split())))

    # Find the longest increasing subsequence (LIS) of the preferences matrix
    dp = [[0] * S for _ in range(M)]
    lis_lengths = [1] * M

    for i in range(M):
        for j in range(S):
            if i > 0 and preferences[i][j] > preferences[i - 1][j]:
                dp[i][j] = dp[i - 1][j] + 1
            else:
                dp[i][j] = 1

            lis_lengths[i] = max(lis_lengths[i], dp[i][j])

    # The length of the smallest possible set list is S - the length of the longest increasing subsequence
    L = S - max(lis_lengths)

    # Find the actual set list
    selected_songs = [0] * L
    selected_songs_index = 0

    for i in range(M):
        for j in range(S - 1, -1, -1):
            if dp[i][j] == lis_lengths[i] + 1:
                selected_songs[selected_songs_index] = j + 1
                selected_songs_index += 1
                # Since this is a set list, we only need to find one instance of each song
                break

    print(L)
    print(*sorted(selected_songs))

solve()

