    def find_unsorted_subarray(arr):
        n = len(arr)
        left = -1
        right = -1

        # Find the first element that is out of order
        for i in range(n - 1):
            if arr[i] > arr[i + 1]:
                left = i
                break

        if left == -1:
            print("impossible")
            return

        # Find the last element that is out of order
        for i in range(n - 1, 0, -1):
            if arr[i] < arr[i - 1]:
                right = i
                break

        # Find the minimum and maximum in the unsorted subarray
        subarray = arr[left:right+1]
        min_val = min(subarray)
        max_val = max(subarray)

        # Extend the subarray to include any numbers less than the minimum or greater than the maximum
        for i in range(left):
            if arr[i] > min_val:
                left = i
                break

        for i in range(right + 1, n):
            if arr[i] < max_val:
                right = i
                break

        print(f"{left + 1} {right + 1}")

