    def print_sum(l, r):
        print(sum(a[l - 1:r]))

    def reverse(k):
        nonlocal a
        start = (k - 1) * (1 << k)
        end = k * (1 << k)
        a[start:end] = a[start:end][::-1]

    def swap(k):
        nonlocal a
        start1 = (2 * k - 2) * (1 << k)
        end1 = (2 * k - 1) * (1 << k)
        start2 = (2 * k - 1) * (1 << k)
        end2 = 2 * k * (1 << k)
        a[start1:end1], a[start2:end2] = a[start2:end2], a[start1:end1]

    for _ in range(q):
        t, *args = input().split()
        if t == '1':
            x, k = map(int, args)
            a[x - 1] = k
        elif t == '2':
            k = int(args[0])
            reverse(k)
        elif t == '3':
            k = int(args[0])
            swap(k)
        elif t == '4':
            l, r = map(int, args)
            print_sum(l, r)

