def solve():
    n, m = map(int, input().split())
    matrix = []
    for _ in range(n):
        matrix.append(list(map(int, input().split())))

    def is_palindrome(matrix):
        n = len(matrix)
        m = len(matrix[0])

        for i in range(n):
            if matrix[i][0] != matrix[i][m-1]:
                return False

        for j in range(m):
            if matrix[0][j] != matrix[n-1][j]:
                return False

        for i in range(1, n):
            if matrix[i][0] != matrix[i-1][m-1]:
                return False

        for j in range(1, m):
            if matrix[0][j] != matrix[n-1][j-1]:
                return False

        return True

    def flip(matrix, i, j):
        n = len(matrix)
        m = len(matrix[0])

        for x in range(i, n):
            for y in range(m):
                matrix[x][y] = 1 - matrix[x][y]

        for x in range(n):
            for y in range(j, m):
                matrix[x][y] = 1 - matrix[x][y]

        for x in range(i, n):
