        stack = [(1, 0)]  # (room number, exit number)
        visited = set()
        while stack:
            room, exit_num = stack.pop()
            if room == n:
                return True
            if room in visited:
                continue
            visited.add(room)
            if config[room - 1][exit_num - 1] == 0:
                continue
            next_room = config[room - 1][exit_num - 1] - 1
            stack.append((next_room, 0))
            stack.append((room, exit_num + 1))
        return False

    if not can_reach_dormitory(config_senior):
        print("Impossible")
    elif can_reach_dormitory(config_you):
        print("Yes")
    else:
