def solve():
    n, m, k = map(int, input().split())
    maze = []
    for _ in range(n):
        maze.append(input())

    x_row = 0
    x_col = 0
    for i in range(n):
        if "X" in maze[i]:
            x_row = i
            x_col = maze[i].index("X")
            break

    ways = []
    directions = ["L", "R", "U", "D"]

    def is_valid(x, y):
        if 0 <= x < n and 0 <= y < m and maze[x][y] != "*":
            return True
        return False

    def backtrack(x, y, path, steps):
        if steps == k:
            ways.append(path)
            return

        for direction in directions:
            new_x = x + {"L": -1, "R": 1, "U": -1, "D": 1}[direction]
            new_y = y + {"L": 0, "R": 0, "U": 1, "D": -1}[direction]

            if is_valid(new_x, new_y):
                backtrack(new_x, new_y, path + direction, steps + 1)

    backtrack(x_row, x_col, "", 0)

    if not ways:
        print("IMPOSSIBLE")
    else:
        print(min(ways))

