def factorial(n):
    if n == 0:
        return 1
    result = 1
    for i in range(1, n + 1):
        result = (result * i) % MOD
    return result

def combination(n, r):
    if r > n:
        return 0
    r = min(r, n - r)
    return (factorial(n) * pow(factorial(r), MOD - 2, MOD) * pow(factorial(n - r), MOD - 2, MOD)) % MOD

def sum_of_partitions(n, k):
    if k == 0:
        return 0
    if k == 1:
        return sum(w) ** n

    total_sum = 0
    for i in range(1, n + 1):
        remaining_sum = sum(w) - w[i - 1]
        remaining_n = n - 1
        total_sum = (total_sum + combination(n, i) * pow(2, i - 1, MOD) * sum_of_partitions(remaining_n, k - 1) * pow(remaining_sum, i - 1, MOD)) % MOD

    return total_sum

