        if grid[n-1][m-1] != 'C':
            return False

        stack = [(n-1, m-1)]
        visited = set()
        visited.add((n-1, m-1))

        while stack:
            i, j = stack.pop()

            if i > 0 and grid[i-1][j] == 'R' and (i-1, j) not in visited:
                stack.append((i-1, j))
                visited.add((i-1, j))
            if j < m - 1 and grid[i][j+1] == 'D' and (i, j+1) not in visited:
                stack.append((i, j+1))
                visited.add((i, j+1))
            if i < n - 1 and grid[i+1][j] == 'D' and (i+1, j) not in visited:
                stack.append((i+1, j))
                visited.add((i+1, j))
            if j > 0 and grid[i][j-1] == 'R' and (i, j-1) not in visited:
                stack.append((i, j-1))
                visited.add((i, j-1))

        return True

    if is_functional(grid):
        print(0)
