def solve():
    R, C = map(int, input().split())
    maze = [input() for _ in range(R)]

    odd_squares = []
    even_squares = []

    for r in range(R):
        for c in range(C):
            if (r + c) % 2 == 0:
                even_squares.append((r, c))
            else:
                odd_squares.append((r, c))

    def is_outside(r, c):
        if r < 0 or r >= R or c < 0 or c >= C:
            return True
        return False

    def dfs(r, c, visited):
        if is_outside(r, c):
            return False
        if (r, c) in visited:
            return True
        visited.add((r, c))
        if maze[r][c] == '/':
            if not (is_outside(r - 1, c + 1) or (r - 1, c + 1) in visited):
                if not dfs(r - 1, c + 1, visited):
                    return False
            if not (is_outside(r + 1, c - 1) or (r + 1, c - 1) in visited):
                if not dfs(r + 1, c - 1, visited):
                    return False
        else:
            if not (is_outside(r - 1, c - 1) or (r - 1, c - 1) in visited):
                if not dfs(r - 1, c - 1, visited):
                    return False
            if not (is_outside(r + 1, c + 1) or (r + 1, c + 1) in visited):
                if not dfs(r + 1, c + 1, visited):
                    return False
        return True

    def check_escape():
        visited = set()
        for r, c in odd_squares:
            if (r, c) not in visited:
                if not dfs(r, c, visited):
                    return False
        for r, c in even_squares:
            if (r, c) not in visited:
                if not dfs(r, c, visited):
                    return False
        return True

    if check_escape():
        print(0)
        return

    def flip_wall(r, c):
        if maze[r][c] == '/':
            maze[r][c] = '\\'
        else:
            maze[r][c] = '/'

    def count_walls():
        count = 0
        for r in range(R):
            for c in range(C):
                if maze[r][c] == '/':
                    count += 1
                elif maze[r][c] == '\\':
                    count += 1
        return count

    for r in range(R):
        for c in range(C):
            if maze[r][c] == '/':
                flip_wall(r, c)
                if check_escape():
                    print(count_walls() - 1)
                    return
                flip_wall(r, c)
            elif maze[r][c] == '\\':
                flip_wall(r, c)
                if check_escape():
                    print(count_walls() - 1)
                    return
                flip_wall(r, c)

solve()

