def solve():
    n, m = map(int, input().split())
    s = input()

    dp = {}

    def find_min_moves(current_square, moves):
        if current_square == n:
            return moves if moves < float('inf') else float('inf')

        if current_square in dp:
            if moves > dp[current_square]:
                return float('inf')
            else:
                return dp[current_square]

        min_moves = float('inf')

        for i in range(m):
            next_square = current_square + i + 1
            if next_square <= n:
                min_moves = min(min_moves, find_min_moves(next_square, moves + 1))

        if s[current_square] == '1':
            min_moves = min(min_moves, find_min_moves(n, moves + 1))

        dp[current_square] = min_moves
        return min_moves

    result = find_min_moves(0, 0)

    if result == float('inf'):
        print("-1")
    else:
        path = []
        def backtrack(current_square, moves):
            if current_square == n:
                if moves < result:
                    result = moves
                    path.clear()
                elif moves == result:
                    path.append(str(current_square - moves + 1))
                return

            if current_square in dp:
                if moves > dp[current_square]:
                    return
                else:
                    backtrack(current_square, dp[current_square])

            for i in range(m):
                next_square = current_square + i + 1
                if next_square <= n:
                    backtrack(next_square, moves + 1)

            if s[current_square] == '1':
                backtrack(n, moves + 1)

        backtrack(0, 0)

        print(*sorted(path))

