def solve():
    n, m, k = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(input())

    def get_neighbors(x, y, z):
        neighbors = []
        if x > 0:
            neighbors.append((x - 1, y, z))
        if x < n - 1:
            neighbors.append((x + 1, y, z))
        if y > 0:
            neighbors.append((x, y - 1, z))
        if y < m - 1:
            neighbors.append((x, y + 1, z))
        if z > 0:
            neighbors.append((x, y, z - 1))
        if z < k - 1:
            neighbors.append((x, y, z + 1))
        return neighbors

    def dfs(x, y, z, visited):
        if (x, y, z) in visited:
            return False
        visited.add((x, y, z))
        neighbors = get_neighbors(x, y, z)
        for nx, ny, nz in neighbors:
            if grid[nx][ny] == '0' or (nx, ny, nz) in visited:
                continue
            if not dfs(nx, ny, nz, visited):
                return False
        return True

    critical_count = 0
    for i in range(n):
        for j in range(m):
            for k in range(k):
                if grid[i][j] == '0':
                    continue
                visited = set()
                if dfs(i, j, k, visited):
                    critical_count += 1

    print(critical_count)

