import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def average(lst):
    return sum(lst) / len(lst)

def parse_image(image):
    touches = []
    for y in range(30):
        row = []
        for x in range(15):
            if image[y*15 + x] == 'X':
                row.append((x, y))
        if row:
            touches.append(row)
    return touches

def find_grip(touches):
    points = []
    for y in range(30):
        for x in range(15):
            if touches[y][x]:
                points.append((x, y))
    return [(average([p[0] for p in points]), average([p[1] for p in points]))]

def find_touch_vectors(grip):
    points = [(x, y) for (x, y) in grip[0] for _ in range(len(grip))]
    return [(points[i][0] - points[i+1][0], points[i][1] - points[i+1][1]) for i in range(len(points) - 1)]

def find_grip_rotation(grip):
    vectors = find_touch_vectors(grip)
    if not vectors:
        return 0
    x1, y1 = grip[0][0]
    x2, y2 = grip[0][-1]
    cx = (x1 + x2) / 2
    cy = (y1 + y2) / 2
    rotation = 0
    for i in range(len(vectors)):
        x, y = vectors[i]
        rotation += math.atan2(y - cy, x - cx)
    return rotation

def find_grip_spread(grip):
    points = [(x, y) for (x, y) in grip[0] for _ in range(len(grip))]
    x_coords = [p[0] for p in points]
    y_coords = [p[1] for p in points]
    return (max(x_coords) - min(x_coords) + max(y_coords) - min(y_coords)) / 2

def find_pan_distance(grip1, grip2):
    return distance(grip1[0], grip2[0])

def find_zoom_distance(grip1, grip2):
    spread1 = find_grip_spread(grip1)
    spread2 = find_grip_spread(grip2)
    return abs(spread2 - spread1)

def find_rotation_distance(grip1, grip2):
    vectors1 = find_touch_vectors(grip1)
    vectors2 = find_touch_vectors(grip2)
    if not vectors1 or not vectors2:
        return 0
    rotation1 = find_grip_rotation(grip1)
    rotation2 = find_grip_rotation(grip2)
    return abs(rotation2 - rotation1)

def classify_gesture(grip1, grip2):
    pan_dist = find_pan_distance(grip1, grip2)
    zoom_dist = find_zoom_distance(grip1, grip2)
    rot_dist = find_rotation_distance(grip1, grip2)

    if pan_dist > zoom_dist and pan_dist > rot_dist:
        return "1 pan"
    elif zoom_dist > pan_dist and zoom_dist > rot_dist:
        spread1 = find_grip_spread(grip1)
        spread2 = find_grip_spread(grip2)
        if spread1 > spread2:
            direction = "out"
        else:
            direction = "in"
        return f"{len(grip1)} zoom {direction}"
    else:
        rotation1 = find_grip_rotation(grip1)
        rotation2 = find_grip_rotation(grip2)
        if rotation1 * rotation2 < 0:
            direction = "clockwise" if rotation1 > 0 else "counter-clockwise"
            return f"{len(grip1)} rotate {direction}"
        else:
            return "1 rotate"

