    nums = []
    for _ in range(n):
        nums.append(int(input()))

    MOD = 10**9 + 7

    dp = {}
    def count_ways(index, prev_gcd):
        if index == n:
            return 1

        key = (index, prev_gcd)
        if key in dp:
            return dp[key]

        ways = 0
        for i in range(n):
            if i != index:
                curr_gcd = gcd(nums[i], prev_gcd)
                if curr_gcd > 1:
                    ways = (ways + count_ways(i, curr_gcd)) % MOD

        dp[key] = ways
        return ways

    print(count_ways(0, 1))

