def solve():
    s = input()
    n = len(s)

    def factorize(s):
        if not s:
            return []

        factors = []
        for i in range(1, n):
            if s[i] == s[i-1]:
                factor = s[i]
                j = i
                while j < n - 1 and s[j+1] == factor:
                    j += 1
                factors.extend(factorize(s[:i] + s[j+1:]))
                i = j
        if not factors:
            factors = [s]

        return factors

    factors = factorize(s)

    def weight(f):
        return len(f) - 2 * sum(f.count('(') + f.count(')') + f.count('^') for f in factors)

    minimal_weight = float('inf')
    minimal_factors = None

    def find_minimal_factors(factors):
        nonlocal minimal_weight
        nonlocal minimal_factors

        if not factors:
            return

        if weight(factors[0]) < minimal_weight:
            minimal_weight = weight(factors[0])
            minimal_factors = [factors[0]]
        elif weight(factors[0]) == minimal_weight:
            minimal_factors.append(factors[0])

        for i in range(len(factors)):
            new_factors = factors[:i] + factors[i+1:]
            find_minimal_factors(new_factors)

    find_minimal_factors(factors)

    if not minimal_factors:
        print(weight([s]))
    else:
        print(minimal_weight)

