def dfs(node, parent, depth, visited, distances):
    visited[node] = True
    distances[node] = depth

    for i in range(M):
        if roads[i][0] == node and i != parent:
            if not visited[roads[i][1]]:
                dfs(roads[i][1], i, depth + 1, visited, distances)
            else:
                distances[roads[i][1]] = min(distances[roads[i][1]], depth + 1)

    visited[node] = False

def find_shortest_paths():
    visited = [False] * N
    distances = [float('inf')] * N

    dfs(0, -1, 0, visited, distances)

    shortest_paths = [0] * M
    for i in range(M):
        if roads[i][0] == 0:
            shortest_paths[i] = 1
        else:
            shortest_paths[i] = 0

    for i in range(N):
        if distances[i] == float('inf'):
            continue

        visited = [False] * N
        distances2 = [float('inf')] * N

        dfs(i, -1, 0, visited, distances2)

        for j in range(M):
            if roads[j][0] == i:
                if distances2[roads[j][1]] == distances[i] + roads[j][2]:
                    shortest_paths[j] += 1
                    shortest_paths[j] %= MOD

    return shortest_paths

