    n, m = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(input())

    new_grid = [['.' for _ in range(m)] for _ in range(n)]

    def is_valid(x, y):
        if x < 0 or x >= n or y < 0 or y >= m:
            return False
        return grid[x][y] == '.'

    def place_shop(x, y):
        if not is_valid(x, y):
            return
        if new_grid[x][y] == 'E':
            return

        new_grid[x][y] = 'E'

        # Check adjacent cells
        place_shop(x - 1, y)  # West
        place_shop(x + 1, y)  # East
        place_shop(x, y - 1)  # North
        place_shop(x, y + 1)  # South

    for i in range(n):
        for j in range(m):
            if grid[i][j] == '.':
                place_shop(i, j)

    for row in new_grid:
        print("".join(row))

