def is_squarefree(num):
    if num < 2:
        return True

    i = 2
    while i * i <= num:
        if num % i == 0:
            if num // i == i:
                return False
            else:
                while num % i == 0:
                    num //= i
        i += 1

    if num > 1:
        if num % i == 0:
            return False
        else:
            while num % i == 0:
                num //= i
    return True

