    # Check if the graph is connected
    visited = [False] * (n + 1)
    visited[1] = True
    stack = [1]
    while stack:
        node = stack.pop()
        for u, v in edges:
            if u == node and not visited[v]:
                visited[v] = True
                stack.append(v)
            elif v == node and not visited[u]:
                visited[u] = True
                stack.append(u)

    if not all(visited):
        print("NO")
        return

    # Try to orient the edges
    orientation = []
    for u, v in edges:
        if visited[u] > visited[v]:
            orientation.append("0")
