    rain_segments.sort()
    umbrellas.sort()

    i = 0
    j = 0
    total_fatigue = 0
    while i < len(umbrellas):
        x, p = umbrellas[i]

        while j < len(rain_segments) and rain_segments[j][0] <= x:
            l, r = rain_segments[j]
            if l <= x and x + 1 <= r:
                i += 1
                if i == len(umbrellas):
