def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    def calculate_max_result(arrangement):
        leaves = []
        children = [[] for _ in range(n + 1)]
        for u, v in edges:
            children[u].append(v)

        def dfs(node, path):
            if not children[node]:
                leaves.append((node, path[-1]))
            else:
                for child in children[node]:
                    dfs(child, path + [arrangement[child - 1]])

        dfs(1, [])
        return max([leaf[1] for leaf in leaves])

    def calculate_min_result(arrangement):
        leaves = []
        children = [[] for _ in range(n + 1)]
        for u, v in edges:
            children[u].append(v)

        def dfs(node, path):
            if not children[node]:
                leaves.append((node, path[-1]))
            else:
                for child in children[node]:
                    dfs(child, path + [arrangement[child - 1]])

        dfs(1, [])
        return min([leaf[1] for leaf in leaves])

    max_result = 0
    min_result = float('inf')

    import itertools

    for perm in itertools.permutations(range(1, n + 1)):
        max_result = max(max_result, calculate_max_result(list(perm)))
        min_result = min(min_result, calculate_min_result(list(perm)))

    print(max_result, min_result)

