def closest_pair(points):
    n = len(points)
    if n <= 3:
        min_dist = float('inf')
        for i in range(n):
            for j in range(i + 1, n):
                dist = distance_squared(points[i], points[j])
                if dist < min_dist:
                    min_dist = dist
        return math.sqrt(min_dist)

    mid = n // 2
    mid_point = points[mid]

    left_points = points[:mid]
    right_points = points[mid:]

    left_min = closest_pair(left_points)
    right_min = closest_pair(right_points)

    min_dist = min(left_min, right_min)

    strip = []
    for point in points:
        if abs(point[0] - mid_point[0]) < min_dist:
            strip.append(point)

    strip_min = float('inf')
    for i in range(len(strip)):
        for j in range(i + 1, len(strip)):
            dist = distance_squared(strip[i], strip[j])
            if dist < strip_min:
                strip_min = dist

    return math.sqrt(min(min_dist, strip_min))

