        return max_consecutive

    def find_leaves(root, parent, visited, path):
        visited[root - 1] = True
        if len(path) > 0:
            path.append(root)
        else:
            path = [root]

        if len([e for e in edges if e[0] == root]) == 1:
            leaves.append(path[:])
        else:
            for edge in edges:
                if edge[0] == root and edge[1] != parent:
                    find_leaves(edge[1], root, visited, path[:])
                elif edge[1] == root and edge[0] != parent:
                    find_leaves(edge[0], root, visited, path[:])

