        if p1[1] > p2[1]:
            p1, p2 = p2, p1
        return True

    def find_max_lifts(pylons):
        n = len(pylons)
        adj = [[] for _ in range(n)]
        one_way_pylons = []
        two_way_pylons = []

        for i in range(n):
            if pylons[i][2] == 1:
                one_way_pylons.append(i)
            else:
                two_way_pylons.append(i)

        def add_edge(u, v):
            adj[u].append(v)
            adj[v].append(u)

        def remove_edge(u, v):
            adj[u].remove(v)
            adj[v].remove(u)

        def dfs(node, visited, component):
            visited[node] = True
            component.append(node)
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    dfs(neighbor, visited, component)

        def is_connected(component):
            if not component:
                return True
            visited = [False] * n
            dfs(component[0], visited, [])
            for node in component:
                if not visited[node]:
                    return False
            return True

        def check_two_way_constraints(component):
            for i in component:
                if pylons[i][2] == 2:
                    neighbors = [j for j in range(n) if j != i and (j, i) in edges]
                    if len(neighbors) != 2:
                        return False
                    else:
                        neighbor1, neighbor2 = neighbors[0], neighbors[1]
                        if (neighbor1, i) not in edges or (i, neighbor1) not in edges:
                            return False
                        if (neighbor2, i) not in edges or (i, neighbor2) not in edges:
                            return False
                        if pylons[neighbor1][1] == pylons[i][1] or pylons[neighbor2][1] == pylons[i][1]:
                            return False
            return True

        max_lifts = 0
        for i in range(1 << len(one_way_pylons)):
            one_way_edges = []
            for j in range(len(one_way_pylons)):
                if (i >> j) & 1:
                    one_way_edges.append((one_way_pylons[j], one_way_pylons[(j + 1) % len(one_way_pylons)]))

            for j in range(1 << len(two_way_pylons)):
                two_way_edges = []
                for k in range(len(two_way_pylons)):
                    if (j >> k) & 1:
                        two_way_edges.append((two_way_pylons[k], two_way_pylons[(k + 1) % len(two_way_pylons)]))

                edges = one_way_edges + two_way_edges

                if len(edges) > 0:
                    graph = [[] for _ in range(n)]
                    for u, v in edges:
