                    visited = [False] * n
                    components = []
                    for node in range(n):
                        if not visited[node]:
                            component = []
                            dfs(node, visited, component)
                            components.append(component)

                    valid_components = []
                    for component in components:
                        if is_connected(component) and check_two_way_constraints(component):
                            valid_components.append(component)

                    max_lifts = max(max_lifts, len(edges))

        return max_lifts

