def solve():
    n, p, k = map(int, input().split())
    pipes = []
    for _ in range(p):
        a, b, c = map(int, input().split())
        pipes.append((a, b, c))

    def max_water():
        graph = [[] for _ in range(n + 1)]
        for a, b, c in pipes:
            graph[a].append((b, c))
            graph[b].append((a, c))

        dist = [float('inf')] * (n + 1)
        dist[1] = 0

        pq = [(0, 1)]

        while pq:
            d, u = heapq.heappop(pq)
            if d > dist[u]:
                continue
            for v, c in graph[u]:
                if dist[u] + c < dist[v]:
                    dist[v] = dist[u] + c
                    heapq.heappush(pq, (dist[v], v))

        return dist[n]

    print(max_water())

    for _ in range(k):
        a, b, c = map(int, input().split())

        new_pipes = pipes + [(a, b, c)]

        max_dist = float('-inf')

        for i in range(1 << len(new_pipes)):
            temp_graph = [[] for _ in range(n + 1)]

            for j, (a, b, c) in enumerate(new_pipes):
                if (i >> j) & 1:
                    temp_graph[a].append((b, c))
                    temp_graph[b].append((a, c))

            dist = [float('inf')] * (n + 1)
            dist[1] = 0

            pq = [(0, 1)]

            while pq:
                d, u = heapq.heappop(pq)
                if d > dist[u]:
                    continue
                for v, c in temp_graph[u]:
                    if dist[u] + c < dist[v]:
                        dist[v] = dist[u] + c
                        heapq.heappush(pq, (dist[v], v))

            max_dist = max(max_dist, dist[n])

        print(max_dist)

