    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))

    MOD = 998244353

    def count_partitions(arr, target):
        count = 0
        dp = [0] * (target + 1)
        dp[0] = 1

        for num in arr:
            for i in range(target, num - 1, -1):
                dp[i] = (dp[i] + dp[i - num]) % MOD

        return dp[target]

    def find_first_greater_or_equal(arr, target):
        left, right = 0, len(arr) - 1
        result = -1
        while left <= right:
            mid = (left + right) // 2
            if arr[mid] >= target:
                result = mid
                right = mid - 1
            else:
                left = mid + 1
        return result

    def find_last_less_or_equal(arr, target):
        left, right = 0, len(arr) - 1
        result = -1
        while left <= right:
            mid = (left + right) // 2
            if arr[mid] <= target:
                result = mid
                left = mid + 1
            else:
                right = mid - 1
        return result

    def count_valid_partitions(arr, b):
        n = len(arr)
        m = len(b)

        if n < m:
            return 0

        dp = {}

        def solve(index, last_b_index):
            if (index, last_b_index) in dp:
                return dp[(index, last_b_index)]

            if index == n:
                return 1

            if last_b_index == m - 1:
                first_greater_or_equal = find_first_greater_or_equal(arr, b[last_b_index])
                if first_greater_or_equal == n:
                    return 1
                else:
                    return 0

            count = 0
            first_greater_or_equal = find_first_greater_or_equal(arr, b[last_b_index + 1])

            if first_greater_or_equal == n:
                return 0

            for i in range(first_greater_or_equal, n):
                if arr[i] == b[last_b_index + 1]:
                    count = (count + solve(i + 1, last_b_index + 1)) % MOD
            dp[(index, last_b_index)] = count
            return count

        return solve(0, -1)

    result = count_valid_partitions(a, b)
    print(result)

solve()

