class Solution:
    def majorityElement(self, nums: List[int]) -> List[int]:
        if not nums:
            return []

        candidate1 = None
        candidate2 = None
        count1 = 0
        count2 = 0

        for num in nums:
            if candidate1 == num:
                count1 += 1
            elif candidate2 == num:
                count2 += 1
            elif count1 == 0:
                candidate1 = num
                count1 = 1
            elif count2 == 0:
                candidate2 = num
                count2 = 1
            else:
                count1 -= 1
                count2 -= 1

        result = []
        if self.checkMajority(nums, candidate1):
            result.append(candidate1)
        if candidate1 != candidate2 and self.checkMajority(nums, candidate2):
            result.append(candidate2)

        return result

    def checkMajority(self, nums, candidate):
        count = 0
        for num in nums:
            if num == candidate:
                count += 1
        return count > len(nums) // 3

