def solve():
    n, q = map(int, input().split())
    a = list(map(int, input().split()))

    def replace(x, k):
        a[x-1] = k

    def reverse(k):
        for i in range(1, n + 1):
            start = (i - 1) * (2**k) + 1
            end = i * (2**k)
            a[start-1:end] = a[start-1:end][::-1]

    def swap(k):
        for i in range(1, n + 1):
            start1 = (2*i - 2) * (2**k) + 1
            end1 = (2*i - 1) * (2**k)
            start2 = (2*i - 1) * (2**k) + 1
            end2 = 2*i * (2**k)
            a[start1-1:end1], a[start2-1:end2] = a[start2-1:end2], a[start1-1:end1]

    def sum_query(l, r):
        return sum(a[l-1:r])

    for _ in range(q):
        query = input().split()
        query_type = query[0]
        if query_type == '1':
            x = int(query[1])
            k = int(query[2])
            replace(x, k)
        elif query_type == '2':
            k = int(query[1])
            reverse(k)
        elif query_type == '3':
            k = int(query[1])
            swap(k)
        elif query_type == '4':
            l = int(query[1])
            r = int(query[2])
            print(sum_query(l, r))

