def solve():
    n, m, k = map(int, input().split())
    maze = []
    for _ in range(n):
        maze.append(input())

    start_row = -1
    start_col = -1
    for i in range(n):
        for j in range(m):
            if maze[i][j] == 'X':
                start_row = i
                start_col = j
                break
        if start_row != -1:
            break

    def is_valid(row, col):
        return 0 <= row < n and 0 <= col < m and maze[row][col] != '*'

    def find_min_path(start_row, start_col, k):
        min_path = None

        def dfs(row, col, path):
            nonlocal min_path

            if len(path) == k:
                if [row, col] == [start_row, start_col]:
                    if min_path is None or path < min_path:
                        min_path = path
                return

            # Explore all possible moves
            for dr, dc in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                new_row = row + dr
                new_col = col + dc
                if is_valid(new_row, new_col):
                    dfs(new_row, new_col, path + maze[new_row][new_col])

        for dr, dc in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            new_row = start_row + dr
            new_col = start_col + dc
            if is_valid(new_row, new_col):
                dfs(new_row, new_col, maze[new_row][new_col])

        return min_path

    path = find_min_path(start_row, start_col, k)

    if path is None:
        print("IMPOSSIBLE")
    else:
        print(path)

solve()

