def solve():
    n, k = map(int, input().split())
    w = list(map(int, input().split()))

    MOD = 10**9 + 7

    def calculate_weight(subsets):
        total_weight = 0
        for subset in subsets:
            subset_weight = sum(w[i-1] for i in subset)
            total_weight = (total_weight + len(subset) * subset_weight) % MOD
        return total_weight

    def generate_partitions(index, current_partition, remaining_elements):
        if index == n + 1:
            if len(current_partition) == k:
                if len(current_partition[-1]) > 0:
                    return [current_partition]
                else:
                    return []
            else:
                return []

        partitions = []
        for i in range(len(current_partition)):
            new_partition = current_partition[:]
            new_partition[i].add(index)
            partitions.extend(generate_partitions(index + 1, new_partition, remaining_elements))

        if len(current_partition) < k:
            new_partition = current_partition + [set([index])]
            partitions.extend(generate_partitions(index + 1, new_partition, remaining_elements))

        return partitions

    all_partitions = generate_partitions(1, [set() for _ in range(k)], set(range(1, n + 1)))

    total_weight = 0
    for partition in all_partitions:
        total_weight = (total_weight + calculate_weight(partition)) % MOD

    print(total_weight)

solve()

