    min_changes = float('inf')

    for new_grid in itertools.product(['R', 'D'], repeat=n * m):
        new_grid = [list(new_grid[i * m:(i + 1) * m]) for i in range(n)]

        if new_grid[n-1][m-1] != 'C':
            continue

        if is_functional(new_grid):
            changes = 0
            for i in range(n):
                for j in range(m):
                    if grid[i][j] != new_grid[i][j]:
                        changes += 1
            min_changes = min(min_changes, changes)

    print(min_changes)

