                continue
            region.add((cx, cy))
            for nx, ny in [(cx + 1, cy), (cx - 1, cy), (cx, cy + 1), (cx, cy - 1)]:
                if 0 <= nx < r and 0 <= ny < c and (nx, ny) not in region:
                    if maze[nx][ny] == '.':
                        q.append((nx, ny))
                    elif (nx + ny) % 2 == 0:
                        q.append((nx, ny))
                    else:
                        q.append((nx, ny))
        return region

