                    if (i + j) % 2 != 0:
                        walls_to_remove += 1
        return walls_to_remove

    def check_all_regions_reachable():
        for i in range(len(regions)):
            for j in range(i + 1, len(regions)):
                if not is_reachable(regions[i], regions[j]):
                    return False
        return True

