    def find_root(edges, n):
        adj = [[] for _ in range(n)]
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        for i in range(n):
            if len(adj[i]) == n - 1:
                return i
        return 0

    root = find_root(edges, n)

    def dfs(node, parent, x, y, visited, adj):
        visited[node] = True
        coords[node] = (x, y)

        for neighbor in adj[node]:
            if not visited[neighbor] and neighbor != parent:
                if x == 0:
                    dfs(neighbor, node, x, y + 1, visited, adj)
                elif y == 0:
                    dfs(neighbor, node, x + 1, y, visited, adj)
                else:
                    if abs(x) > abs(y):
                        dfs(neighbor, node, x + 1, y, visited, adj)
                    else:
                        dfs(neighbor, node, x, y + 1, visited, adj)

    coords = [None] * n
    visited = [False] * n

    dfs(root, -1, 0, 0, visited, adj)

    if all(coords):
