        positions = [frog[0] for frog in frogs]
        distances = [frog[1] for frog in frogs]

        max_pos = max(positions)
        min_pos = min(positions)

        best_pos = -1
        best_size = 0

        for start_pos in range(min_pos, max_pos + 1):
            possible_tower = []
            for i in range(len(frogs)):
                curr_pos = frogs[i][0]
                curr_dist = frogs[i][1]
