    def find_divisors(num):
        divisors = []
        for i in range(2, int(num**0.5) + 1):
            if num % i == 0:
                divisors.append(i)
                if i * i != num:
                    divisors.append(num // i)
        if num > 1:
            divisors.append(num)
        return sorted(list(set(divisors)))

    def check_good_pair(i, j):
        return (i + 1 + j + 1) % 2 != 0

    def find_common_divisors(arr1, arr2):
        divisors = []
        for d in find_divisors(arr1[0]):
            if d > 1 and arr1[0] % d == 0 and arr2[0] % d == 0:
                divisors.append(d)
        return sorted(list(set(divisors)))

    def find_max_operations():
