def solve():
    n, m = map(int, input().split())
    cities = [input() for _ in range(n)]

    adj = {city: [] for city in cities}
    for _ in range(m):
        city1, city2, cost = input().split()
        cost = int(cost)
        adj[city1].append((city2, cost))
        adj[city2].append((city1, cost))

    assignments = []
    for _ in range(4):
        assignments.append(tuple(input().split()))

    def find_path(start, end, visited, path):
        visited[start] = True
        path.append(start)

        if start == end:
            return path

        for neighbor, _ in adj[start]:
            if not visited[neighbor]:
                result = find_path(neighbor, end, visited.copy(), path.copy())
                if result:
                    return result

        path.pop()
        return None

    def calculate_cost(path):
        cost = 0
        for i in range(len(path) - 1):
            cost += adj[path[i]][i][1]
        return cost

    min_cost = float('inf')

    import itertools

    for assignment_combination in itertools.permutations(assignments):
        current_cost = 0
        visited = {city: False for city in cities}

        for assignment in assignment_combination:
            path = find_path(assignment[0], assignment[1], visited.copy(), [])
            if path:
                current_cost += calculate_cost(path)
                for city in path:
                    visited[city] = True
            else:
                current_cost = float('inf')
                break

        min_cost = min(min_cost, current_cost)

    print(min_cost)

solve()

