                counts[color] = 0
            counts[color] += 1

        def is_valid(arrangement):
            for i in range(len(arrangement) - 1):
                if arrangement[i] in forbidden and arrangement[i+1] in forbidden:
                    return False
            return True

        def count_sequences(arrangement, sequence):
            count = 0
            for i in range(len(arrangement) - len(sequence) + 1):
                sub_arrangement = arrangement[i:i+len(sequence)]
                if sub_arrangement == sequence:
                    count += 1
            return count

        def generate_arrangements(current_arrangement, remaining_colors):
            if not remaining_colors:
                if is_valid(current_arrangement):
                    return [current_arrangement]
                else:
                    return []

            valid_arrangements = []
            for color, count in remaining_colors.items():
                new_arrangement = current_arrangement + [color] * count
                new_remaining_colors = remaining_colors.copy()
                new_remaining_colors[color] -= count
                if new_remaining_colors[color] == 0:
                    del new_remaining_colors[color]

                valid_arrangements.extend(generate_arrangements(new_arrangement, new_remaining_colors))

            return valid_arrangements

        all_arrangements = generate_arrangements([], counts)

        total_sequences = 0
        for arrangement in all_arrangements:
            total_sequences += count_sequences(arrangement, sequence)

        return total_sequences % 1000000007

    result = count_arrangements(colors, forbidden, sequence)
    print(result)

