    for _ in range(n - 1):
        a, b = map(int, input().split())
        edges.append((a - 1, b - 1))

    def distance_squared(p1, p2):
        return (p1[0] - p2[0]) ** 2 + (p1[1] - p2[1]) ** 2

    def check_solution(points):
        for u, v in edges:
            d = distance_squared(points[u], points[v])
            if not (1 - 1e-6 <= d <= 1 + 1e-6):
                return False
        return True

    def find_closest_point(point, points):
        min_dist = float('inf')
        closest_point = None
        for p in points:
            dist = distance_squared(point, p)
            if dist < min_dist:
                min_dist = dist
                closest_point = p
        return closest_point

    def solve_recursive(points, visited):
        if len(visited) == n:
            return points

        last_point = points[visited[-1]]
        candidates = []
        for i in range(n):
            if i not in visited:
                candidates.append(i)

        for candidate in candidates:
            new_points = points[:]
            new_points[candidate] = [last_point[0] + 1, last_point[1]]
            new_visited = visited + [candidate]
            if check_solution(new_points):
                result = solve_recursive(new_points, new_visited)
                if result is not None:
                    return result

        return None

    initial_point = [0, 0]
    points = [initial_point] * n
    points[0] = [0, 0]

    result = solve_recursive(points, [0])
    if result is None:
        print("No valid solution found")
        return

    for point in result:
        print(f"{point[0]:.8f} {point[1]:.8f}")

solve()

