    def calculate_article_length(proof_index, visited):
        if proof_index in visited:
            return float('inf')

        visited.add(proof_index)

        proof = proofs[proof_index]
        length = calculate_length(proof)

        dependencies = calculate_dependencies(proof)

        min_length = float('inf')
        for dep_index in dependencies:
            if dep_index not in visited:
                min_length = min(min_length, calculate_article_length(dep_index, visited.copy()))

        return length + min_length

    min_article_length = float('inf')
    for i in range(n):
        min_article_length = min(min_article_length, calculate_article_length(i, set()))

    print(min_article_length)

